        // ===== SOLO CHECKBOXES PARA COLORES =====
        CheckBox cbOriginal = new CheckBox("Original");
        CheckBox cbVerde = new CheckBox("Verde");
        CheckBox cbAzul = new CheckBox("Azul");
        CheckBox cbAmarillo = new CheckBox("Amarillo");
        CheckBox cbRosa = new CheckBox("Negro");
        CheckBox cbNaranja = new CheckBox("Naranja");

        // Configurar eventos CORRECTAMENTE - cada CheckBox con su propio setOnAction
        cbOriginal.setOnAction(e -> {
            if (cbOriginal.isSelected()) {
                desmarcarOtrosCheckboxes(cbOriginal);
                cambiarColor(pathsColoreables, "#afc6e9");
            }
        });

        cbVerde.setOnAction(e -> {
            if (cbVerde.isSelected()) {
                desmarcarOtrosCheckboxes(cbVerde);
                cambiarColor(pathsColoreables, "#00ff00");
            }
        });

        cbAzul.setOnAction(e -> {
            if (cbAzul.isSelected()) {
                desmarcarOtrosCheckboxes(cbAzul);
                cambiarColor(pathsColoreables, "#0000ff");
            }
        });

        cbAmarillo.setOnAction(e -> {
            if (cbAmarillo.isSelected()) {
                desmarcarOtrosCheckboxes(cbAmarillo);
                cambiarColor(pathsColoreables, "#ffff00");
            }
        });

        cbRosa.setOnAction(e -> {
            if (cbRosa.isSelected()) {
                desmarcarOtrosCheckboxes(cbRosa);
                cambiarColor(pathsColoreables, "#000000");
            }
        });

        cbNaranja.setOnAction(e -> {
            if (cbNaranja.isSelected()) {
                desmarcarOtrosCheckboxes(cbNaranja);
                cambiarColor(pathsColoreables, "#ffa500");
            }
        });

        // Contenedor principal solo con CheckBoxes
        VBox container = new VBox(10);
        container.setPadding(new Insets(15));
        container.getChildren().addAll(
                new Label("Color"),
                cbOriginal,
                cbVerde,
                cbAzul,
                cbAmarillo,
                cbRosa,
                cbNaranja
        );

        // Usar BorderPane para organizar dibujo y controles
        BorderPane mainPane = new BorderPane();
        mainPane.setCenter(root);
        mainPane.setRight(container);

        Scene mainScene = new Scene(mainPane, 700, 600, Color.WHITESMOKE);
        primaryStage.setScene(mainScene);
        primaryStage.setTitle("Dibujo3");
        primaryStage.show();
