    // Mtodo para cambiar color
    private void cambiarColor(List<SVGPath> paths, String colorHex) {
        Color nuevoColor = Color.web(colorHex);
        for (SVGPath p : paths) {
            p.setFill(nuevoColor);
        }
    }

    // Mtodo para desmarcar otros checkboxes cuando se selecciona uno
    private void desmarcarOtrosCheckboxes(CheckBox checkboxSeleccionado) {
        // Buscar en el contenedor padre y desmarcar los otros checkboxes
        if (checkboxSeleccionado.getParent() instanceof VBox) {
            VBox parent = (VBox) checkboxSeleccionado.getParent();
            parent.getChildren().forEach(node -> {
                if (node instanceof CheckBox && node != checkboxSeleccionado) {
                    ((CheckBox) node).setSelected(false);
                }
            });
        }
    }

