    public static SVGPath createPath(String d, Color fillcolor, Color stroke, double stroke_width) {
        SVGPath path = new SVGPath();
        path.setContent(d);
        path.setFill(fillcolor);
        path.setStroke(stroke);
        path.setStrokeWidth(stroke_width);
        return path;
    }

    public static void drawShape(Group root, Shape shape) {
        root.getChildren().add(shape);
    }

    public static void drawShape(Group root, Group shapes) {
        root.getChildren().add(shapes);
    }

    public static void drawShape(Group root, Shape shape, Transform[] transforms) {
        shape.getTransforms().addAll(transforms);
        root.getChildren().add(shape);
    }

    public static void drawShape(Group root, Group shapes, Transform[] transforms) {
        shapes.getTransforms().addAll(transforms);
        root.getChildren().add(shapes);
    }

